from collections import defaultdict, deque


# BFS
def minJumps(arr):
    # 1. 构造索引字典
    dSameNums = defaultdict(list)
    for i, a in enumerate(arr):
        dSameNums[a].append(i)

    # 2. 集合记录访问过的点
    visitedIndex = set()
    q = deque()     # 队列 BFS
    q.append([0, 0])
    visitedIndex.add(0)

    # 3. 去掉检查过想等的数
    checkSameSet = set()

    while q:
        idx, step = q.popleft()
        if idx == len(arr) - 1:
            return step

        # +1
        iAddIdx = idx + 1
        if iAddIdx < len(arr) and iAddIdx not in visitedIndex:
            q.append([iAddIdx, step+1])
            visitedIndex.add(iAddIdx)
        # -1
        iSubIdx = idx - 1
        if iSubIdx >= 0 and iSubIdx not in visitedIndex:
            q.append([iSubIdx, step+1])
            visitedIndex.add(iSubIdx)
        # 相同的
        iNum = arr[idx]
        if iNum in checkSameSet:
            continue
        checkSameSet.add(iNum)
        for index in dSameNums.get(iNum, []):
            if index not in visitedIndex:
                q.append([index, step+1])
                visitedIndex.add(index)
    return 0


